sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }var worder = g_form.getValue('u_wm_order');

    var pc = new GlideAjax('autopopcompound');
    pc.addParam('sysparm_name', 'companyname'); // call the function
    pc.addParam('sysparm_company', worder); // pass in userID
    pc.getXML(responseParser);

    function responseParser(response) {
        var answer = response.responseXML.documentElement.getAttribute('answer');
        answer = JSON.parse(answer);
        var company = answer["company"];
        var failuredate = answer["failuredate"];
        var asset = answer["asset"];
        var componentcode = answer["componentcode"];
// 		alert(company);
// 		g_form.addInfoMessage(company);
        g_form.setValue('u_customer_name', company);
        g_form.setValue('u_failure_date', failuredate);
        g_form.setValue('u_equipment', asset);
		g_form.setValue('u_compound_code',componentcode);

    }

}
