sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    var srtcode = g_form.getValue('u_ratetype');
	var claimshours = g_form.getDecimalValue('u_hours_claimed');
    var pc = new GlideAjax('srtcode');
    pc.addParam('sysparm_name', 'srtrate'); // call the function
    pc.addParam('sysparm_ratecard', srtcode); // pass in userID
    pc.getXML(responseParser);

    function responseParser(response) {
        var answer = response.responseXML.documentElement.getAttribute('answer');
		var x = answer * claimshours;
		g_form.setValue('u_claim_amount', x.toString());
    }

}
