sys_script_client.script:
function onLoad() {

    var wo = g_form.getValue('u_wm_order');
    var wot = g_form.getValue('u_wm_ordertask');
    var dealername = g_form.getValue('u_dealer_name');
    var status = g_form.getValue('u_c_status');
    var role = g_user.hasRole('OEM Manager');
    var pc = new GlideAjax('duplicaterecords');
    pc.addParam('sysparm_name', 'duplicate'); // call the function
    pc.addParam('sysparm_wo', wo); // pass in userID
    pc.addParam('sysparm_wot', wot); // pass in userID
    pc.addParam('sysparm_dealername', dealername); // pass in userID
    pc.getXML(responseParser);

    function responseParser(response) {
        var answer = response.responseXML.documentElement.getAttribute('answer');
        if (answer > 1) {
            if (status == 'Under Review' && role == true) {
                g_form.addErrorMessage('⚠️ There is an existing claim from the dealer for this Work Order and Task Combination. Please verify if this is a duplicate one before approval.');
            } 
        }
    }
}
