sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    var x = g_form.getValue('u_wm_order');
    var pc = new GlideAjax('workordervalues');
    pc.addParam('sysparm_name', 'wodetails'); // call the function
    pc.addParam('sysparm_order', x); // pass in userID
    pc.getXML(responseParser);

    function responseParser(response) {
        var answer = response.responseXML.documentElement.getAttribute('answer');
        var ans = JSON.parse(answer);
		g_form.setValue('u_customer_name', ans.company);
		g_form.setValue('u_failure_date', ans.opendate);
		g_form.setValue('u_equipment', ans.asset);
		g_form.setValue('u_component_code', ans.componentcode);
    }

}
