sys_script_include.script:
var srtcode = Class.create();
srtcode.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    srt: function() {
        var sysid = this.getParameter('sysparm_ratecard');
        var rec = new GlideRecordSecure('fm_rate_card');
        rec.addQuery('sys_id', sysid);
        rec.query();
        if (rec.next()) {
            var strdec = rec.short_description.getDisplayValue();
        }
        return strdec;
    },
	srtrate: function() {
        var sysid = this.getParameter('sysparm_ratecard');
        var rec = new GlideRecordSecure('fm_labor_rate_card');
        rec.addQuery('sys_id', sysid);
        rec.query();
        if (rec.next()) {
			var hourcharge = rec.hourly_rate;
        }
        return hourcharge;
    },
	updatestartdate: function() {
        var sysid = this.getParameter('sysparm_contract');
        var rec = new GlideRecordSecure('ast_contract');
        rec.addQuery('sys_id', sysid);
        rec.query();
        if (rec.next()) {
			var startdate = rec.starts;
        }
        return startdate;
    },
	coveragesenddate: function() {
        var sysid = this.getParameter('sysparm_contract');
        var rec = new GlideRecordSecure('ast_contract');
        rec.addQuery('sys_id', sysid);
        rec.query();
        if (rec.next()) {
			var enddate = rec.ends;
        }
        return enddate;
    },
	// workordertask: function() {
    //     var sysid = this.getParameter('sysparm_wot');
    //     var rec = new GlideRecordSecure('x_976837_warrant_0_claims');
    //     rec.addQuery('sys_id', sysid);
    //     rec.query();
    //     if (rec.next()) {
	// 		var enddate = rec.ends;
    //     }
    //     return enddate;
    // },
    type: 'srtcode'
});
