sys_script_include.script:
var duplicaterecords = Class.create();
duplicaterecords.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
duplicate: function() {
        var wo = this.getParameter('sysparm_wo');
        var wot = this.getParameter('sysparm_wot');
        var dealername = this.getParameter('sysparm_dealername');
        var rec = new GlideAggregate('x_976837_warrant_0_claims');
		rec.addAggregate('COUNT', 'u_wm_order');
		rec.addQuery('u_wm_order', wo);
		rec.addQuery('u_wm_ordertask', wot);
		rec.addQuery('u_dealer_name',dealername);
		rec.query();
		while (rec.next()) {
			var x = rec.getAggregate('COUNT', 'u_wm_order');
        }
        return x;
    },
    type: 'duplicaterecords'
});
