sys_script_include.script:
var workordervalues = Class.create();
workordervalues.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    wodetails: function() {
        var wosys_id = this.getParameter('sysparm_order');
        var returndata = {};
        var wodata = new GlideRecord('wm_order');
        wodata.addQuery('sys_id', wosys_id);
        wodata.query();
        if (wodata.next()) {
            returndata.company = wodata.company.getDisplayValue();
            returndata.opendate = wodata.opened_at.getDisplayValue();
            returndata.asset = wodata.asset.toString();
            returndata.componentcode = wodata.asset.model_category.getDisplayValue();
        }
        return new global.JSON().encode(returndata);
    },
    dealerdetails: function() {
        var userid = this.getParameter('sysparm_user');
        var dealer = new GlideRecord('sys_user');
        dealer.addQuery('sys_id', userid);
        dealer.query();
        if (dealer.next()) {
            var dealercompany = dealer.company.toString();
        }
        return dealercompany;
    },
    partswodetails: function() {
        var claimid = this.getParameter('sysparm_id');
        var wo = new GlideRecord('x_976837_warrant_0_claims');
        wo.addQuery('sys_id', claimid);
        wo.query();
        if (wo.next()) {
            var wod = wo.u_wm_ordertask.toString();
        }
        return wod;
    },
    type: 'workordervalues'
});
