sys_ui_action.script:
var sysid = current.sys_id;
// gs.addInfoMessage(sysid);
var parts = new GlideRecord('x_976837_warrant_0_parts');
parts.addQuery('claim_id', sysid);
parts.query();
while (parts.next()) {
    var qltpart = parts.u_partsquantity;
    var clmt = parts.u_claim_amount;
    var parts_total = parseInt(clmt);
//     var parts_total = qltpart * clmt;
    //     gs.addInfoMessage(parts_total);
}

var labour = new GlideRecord('x_976837_warrant_0_labours');
labour.addQuery('claim_id', sysid);
labour.query();
while (labour.next()) {
    var lab_quantity = labour.u_hours_claimed;
    var lab_claimamount = labour.u_claim_amount;

    var lab_total = parseInt(lab_claimamount);
//     var lab_total = lab_quantity * lab_claimamount;

    //     gs.addErrorMessage(lab_total);
}

var other = new GlideRecord('x_976837_warrant_0_other_charges');
other.addQuery('claim_id', sysid);
other.query();
while (other.next()) {
    var other_claims = other.u_claim_amount;
    var totalother_claim = parseInt(other_claims);
    //     gs.addInfoMessage(parseInt(other_claims));
}
var alltotals = parts_total + lab_total + totalother_claim;

var totalclaims = new GlideRecord('x_976837_warrant_0_total_amount');
totalclaims.addQuery('claim_id', sysid);
totalclaims.query();
if (totalclaims.getRowCount() > 0) {
    var total_1 = new GlideRecord('x_976837_warrant_0_total_amount');
    total_1.addQuery('claim_id', sysid);
    total_1.query();
    total_1.next();
	var taxtotal_1 = parseInt(total_1.u_tax);
// 	gs.addInfoMessage(taxtotal_1);
    total_1.deleteRecord();
    totalclaims.initialize();
    totalclaims.claim_id = sysid;
    totalclaims.u_totalpartsamount = parts_total;
    totalclaims.u_totallabouramount = lab_total;
    totalclaims.u_totalothercharges = other_claims;
    totalclaims.u_totalclaimedamount = alltotals;
	totalclaims.u_tax = taxtotal_1;
	totalclaims.u_finalclaimedamount = alltotals + taxtotal_1;
    totalclaims.insert();

} else {
    totalclaims.initialize();
	totalclaims.u_finalclaimedamount = alltotals;
    totalclaims.claim_id = sysid;
    totalclaims.u_totalpartsamount = parts_total;
    totalclaims.u_totallabouramount = lab_total;
    totalclaims.u_totalothercharges = other_claims;
    totalclaims.u_totalclaimedamount = alltotals;
    totalclaims.insert();

}
action.setRedirectURL(current);
if (typeof window == 'undefined')
	current.update();
