sys_ui_policy.script_true:
function onCondition() {
    var status = g_form.getValue('u_c_status');
    if ((status == "Approved") || (status == "Rejected")) {
        g_form.setReadOnly("u_claim_id", true);
        g_form.setReadOnly("u_wm_order", true);
        g_form.setReadOnly("u_wm_ordertask", true);
        g_form.setReadOnly("u_c_status", true);
        g_form.setReadOnly("u_dealer_name", true);
        g_form.setReadOnly("u_customer_name", true);
        g_form.setReadOnly("u_reference_1", true);
        g_form.setReadOnly("u_failure_date", true);
        g_form.setReadOnly("u_equipment", true);
        g_form.setReadOnly("u_component_code", true);
        g_form.setReadOnly("u_active", true);
        g_form.setReadOnly("u_reviewer_note", true);
    } else if (status == "Submitted to OEM") {
        // 		g_form.addInfoMessage("Please Wait for the Reviewer Response");
        g_form.setReadOnly("u_claim_id", true);
        g_form.setReadOnly("u_wm_order", true);
        g_form.setReadOnly("u_wm_ordertask", true);
        g_form.setReadOnly("u_c_status", true);
        g_form.setReadOnly("u_dealer_name", true);
        g_form.setReadOnly("u_customer_name", true);
        // 		g_form.setReadOnly("u_reference_1",true);
        g_form.setReadOnly("u_failure_date", true);
        g_form.setReadOnly("u_reviewer_note", true);
        g_form.setReadOnly("u_equipment", true);
        g_form.setReadOnly("u_component_code", true);
        g_form.setReadOnly("u_active", true);
    }
	else if (status == "Under Review") {
        // 		g_form.addInfoMessage("Please Wait for the Reviewer Response");
        g_form.setReadOnly("u_claim_id", true);
        g_form.setReadOnly("u_wm_order", true);
        g_form.setReadOnly("u_wm_ordertask", true);
        g_form.setReadOnly("u_c_status", true);
        g_form.setReadOnly("u_dealer_name", true);
        g_form.setReadOnly("u_customer_name", true);
        g_form.setReadOnly("u_reference_1",true);
        g_form.setReadOnly("u_failure_date", true);
        g_form.setReadOnly("u_equipment", true);
        g_form.setReadOnly("u_component_code", true);
        g_form.setReadOnly("u_active", true);
//         g_form.setReadOnly("u_reviewer_note", true);
    }
}
